<template>
  <div class="page-container">
    <h1 class="page-title">Game Demo</h1>
    <span class="card-container">
      <Card class="card-content">
        <div class="demo-split">
        <Split>
            <div slot="left" class="left-split-pane">
              <br>
              <h1>Sword</h1>
              <img style="height: 250px; margin-top: 20px" src="data:image/jpeg;base64,␀">
              <br>
              <Button style="width: 70%" @click="orderCreate">Buy</Button>
              <Spin class="spin" v-if="buySpinShow">
                <Icon type="ios-loading" size=25 class="demo-spin-icon-load"></Icon>
              </Spin>
            </div>
            <div slot="right" class="right-split-pane">
              <br>
              <h1>API Log</h1>
              <div class="log-box">
                <p v-for="log in logMessages" >
                  {{log.message}}
                </p>
              </div>

              <Button type="primary" style="width: 75%; margin-top: 2%" @click="clearLogMessage">Clear</Button>
            </div>
        </Split>
        </div>
      </Card>
    </span>
  </div>
</template>

<script>

import {createOrder, submitReceipt} from "@/scripts/api/gameServerAPI";
import {Gcoin} from "@/scripts/contracts/gcoin";
import {vm} from "@/main";

export default {
  name: "GameDemo",
  data(){
    return {
      // logMessages : [{message:"abc"},{message:"def"}],
      logMessages : [],
      orderID: null,
      buySpinShow:false
    }
  },
  methods:{
    orderCreate(){
      this.buySpinShow = true;
      let params = {
        gameItemID:"2",
        itemReceiverUserID:localStorage.getItem('address'),
        paymentAccount: localStorage.getItem('address')
      }
      createOrder(params,this.orderCreateCallback);
    },
    orderCreateCallback(code,msg,data){
      if (code == 0) {
        this.logMessages.push({message:"Created Order:"});
        this.logMessages.push({message: data});
        this.logMessages.push({message: "-----------------------------------------------------------------"});
        this.orderID = data.orderID;
        console.log("orderID: " + this.orderID);
        // console.log(this.logMessages);
        this.transferGcoin(data.gameShopAccount ,data.itemPrice);
      } else if (code == 2){
        this.$Message.error(msg);
      }
    },
    transferGcoin(toAccount,amount){
      Gcoin.start();
      Gcoin.transfer(toAccount,amount,this.transferGcoinCallback,this.transferGcoinErrorCallback);
      this.logMessages.push({message:"Payment account pay for the order (ID: " + this.orderID + ")"});
    },
    transferGcoinCallback(num, receipt){
      vm.$emit('GcoinBalanceUpdate');
      this.logMessages.push({message: receipt});
      this.logMessages.push({message: "-----------------------------------------------------------------"});
      console.log(receipt);
      this.submitReceipt(receipt.transactionHash);
    },
    transferGcoinErrorCallback(num, receipt){

    },
    submitReceipt(transactionHash){
      let params = {
        orderID: this.orderID,
        transactionHash: transactionHash,
      }
      submitReceipt(params,this.submitReceiptCallback);
    },
    submitReceiptCallback(code,msg,data){
      if(code == 0){
        this.logMessages.push({message: "transaction is verified and recorded"});
        this.logMessages.push({message: data});
        this.logMessages.push({message: "-----------------------------------------------------------------"});
        this.logMessages.push({message: "Sword is delivered to player's bag"});
        //finished buy sword process
        this.buySpinShow = false;
        this.$Message.success("Sword is delivered to your bag");
      } else if (code == 2) {
        this.$Message.error(msg);
      }

    },
    clearLogMessage(){
      this.logMessages = []
    }
  }
}
</script>

